Example.JavaScript:
#pragma strict

public class KeyCodeExample extends MonoBehaviour
{
    function Update( )
    {
        if( Input.GetKeyDown( KeyCode.Space ) )
            Debug.Log( "Space key was pressed." );

        if( Input.GetKeyUp( KeyCode.Space ) )
            Debug.Log( "Space key was released." );
    }
}

Example.CSharp:
using UnityEngine;

public class KeyCodeExample : MonoBehaviour
{
    void Update()
    {
        if (Input.GetKeyDown(KeyCode.Space))
            Debug.Log("Space key was pressed.");

        if (Input.GetKeyUp(KeyCode.Space))
            Debug.Log("Space key was released.");
    }
}

Example.JavaScript:
#pragma strict

public class KeyCodeOnGUIExample extends MonoBehaviour
{
    function OnGUI( )
    {
        if( Event.current.Equals( Event.KeyboardEvent( KeyCode.Space.ToString() ) ) )
        Debug.Log( "Space key is pressed." );
    }
}

Example.CSharp:
using UnityEngine;

public class KeyCodeOnGUIExample : MonoBehaviour
{
    void OnGUI()
    {
        if (Event.current.Equals(Event.KeyboardEvent(KeyCode.Space.ToString())))
            Debug.Log("Space key is pressed.");
    }
}
