Example.JavaScript:
// Create a texture and fill it with Perlin noise.
// Try varying the xOrg, yOrg and scale values in the inspector
// while in Play mode to see the effect they have on the noise.

// Width and height of the texture in pixels.
var pixWidth: int;
var pixHeight: int;

// The origin of the sampled area in the plane.
var xOrg: float;
var yOrg: float;

// The number of cycles of the basic noise pattern that are repeated
// over the width and height of the texture.
var scale = 1.0;


private var noiseTex: Texture2D;
private var pix: Color[];
private var rend: Renderer;


function Start () {
    rend = GetComponent.<Renderer>();

    // Set up the texture and a Color array to hold pixels during processing.
    noiseTex = new Texture2D(pixWidth, pixHeight);
    pix = new Color[noiseTex.width * noiseTex.height];
    rend.material.mainTexture = noiseTex;
}


function CalcNoise() {
    // For each pixel in the texture...
    for (var y = 0.0; y < noiseTex.height; y++) {
        for (var x = 0.0; x < noiseTex.width; x++) {
            // Get a sample from the corresponding position in the noise plane
            // and create a greyscale pixel from it.
            var xCoord = xOrg + x / noiseTex.width * scale;
            var yCoord = yOrg + y / noiseTex.height * scale;
            var sample = Mathf.PerlinNoise(xCoord, yCoord);
            pix[y * noiseTex.width + x] = new Color(sample, sample, sample);
        }
    }

    // Copy the pixel data to the texture and load it into the GPU.
    noiseTex.SetPixels(pix);
    noiseTex.Apply();
}


function Update () {
    CalcNoise();
}

Example.CSharp:
using UnityEngine;
using System.Collections;

// Create a texture and fill it with Perlin noise.
// Try varying the xOrg, yOrg and scale values in the inspector
// while in Play mode to see the effect they have on the noise.

public class ExampleScript : MonoBehaviour
{
    // Width and height of the texture in pixels.
    public int pixWidth;
    public int pixHeight;

    // The origin of the sampled area in the plane.
    public float xOrg;
    public float yOrg;

    // The number of cycles of the basic noise pattern that are repeated
    // over the width and height of the texture.
    public float scale = 1.0F;

    private Texture2D noiseTex;
    private Color[] pix;
    private Renderer rend;

    void Start()
    {
        rend = GetComponent<Renderer>();

        // Set up the texture and a Color array to hold pixels during processing.
        noiseTex = new Texture2D(pixWidth, pixHeight);
        pix = new Color[noiseTex.width * noiseTex.height];
        rend.material.mainTexture = noiseTex;
    }

    void CalcNoise()
    {
        // For each pixel in the texture...
        float y = 0.0F;

        while (y < noiseTex.height)
        {
            float x = 0.0F;
            while (x < noiseTex.width)
            {
                float xCoord = xOrg + x / noiseTex.width * scale;
                float yCoord = yOrg + y / noiseTex.height * scale;
                float sample = Mathf.PerlinNoise(xCoord, yCoord);
                pix[(int)y * noiseTex.width + (int)x] = new Color(sample, sample, sample);
                x++;
            }
            y++;
        }

        // Copy the pixel data to the texture and load it into the GPU.
        noiseTex.SetPixels(pix);
        noiseTex.Apply();
    }

    void Update()
    {
        CalcNoise();
    }
}

Example.JavaScript:
// "Bobbing" animation from 1D Perlin noise.

// Range over which height varies.
var heightScale = 1.0;

// Distance covered per second along X axis of Perlin plane.
var xScale = 1.0;


function Update () {
    var height = heightScale * Mathf.PerlinNoise(Time.time * xScale, 0.0);
    var pos = transform.position;
    pos.y = height;
    transform.position = pos;
}

Example.CSharp:
using UnityEngine;
using System.Collections;

public class ExampleClass : MonoBehaviour {
    public float heightScale = 1.0F;
    public float xScale = 1.0F;
    void Update() {
        float height = heightScale * Mathf.PerlinNoise(Time.time * xScale, 0.0F);
        Vector3 pos = transform.position;
        pos.y = height;
        transform.position = pos;
    }
}
