Example.CSharp:
using UnityEngine;
using UnityEngine.Networking;

public class MyScript : NetworkBehaviour
{
    public struct POW
    {
        public int pow;
        public float f;
    }

    public class SyncListPOW : SyncListStruct<POW>
    {
    }

    public SyncListPOW m_pows = new SyncListPOW();
}
